DECLARE @image VARBINARY(MAX)
--- JPG 1x1 white background
SET @image = ;

UPDATE im__Image
SET
  im_Height   = 1,
  im_Width    = 1,
  im_CheckSum =
  CONVERT(VARCHAR(32), HashBytes('MD5', @image), 2),
  im_Image    = @image,
  im_Preview  = @image,
  im_Size = DATALENGTH(@image)
WHERE im_Id >= 1000
